import { Component, OnInit } from '@angular/core';
import { Training } from 'src/app/models/training';
import { TrainingService } from 'src/app/services/training/training.service';

@Component({
  selector: 'app-trainings',
  templateUrl: './trainings.component.html',
  styleUrls: ['./trainings.component.css'],
})
export class TrainingsComponent implements OnInit {
  trainings: Training[] = [];
  training: Training = new Training();
  error!: string;

  constructor(private trainingService: TrainingService) {
    this.training = new Training();
  }

  ngOnInit() {
    this.training = new Training();
    this.trainingService.getTrainings().subscribe({
      next: (data: Training[]) => {
        this.trainings = data;
      },
      error: (error) => {
        this.error =
          "Une erreur s'est produite lors du chargement des articles. /n" +
          error;
      },
    });
    console.log(
      'init : ID ' + this.training.id + '\n ' + 'Name : ' + this.training.name
    );
  }

  createTraining(): void {
    this.trainingService.postTraining(this.training).subscribe({
      next: (data: Training) => {
        this.training = data;
        console.log('createTraining : ' + this.training);
      },
      error: (error) => {
        this.error = "Une erreur s'est produite";
        console.log(error);
        // Gérer l'erreur
      },
    });
  }
}
<div class="container">
    <h1> Sélectionnez des cours.</h1>

    <div class="row">
      <div class="col-md-4"  *ngFor="let training of trainings">
        <div class="card mb-3">
          <img src="https://picsum.photos/300/200" class="card-img-top" alt="Training Image">
          <div class="card-body">
            <h5 class="card-title">Language : {{ training.name }}</h5>
            <p class="card-text">{{ training.description }}</p>
            <p class="card-text">Price: {{ training.price }}</p>
            <p class="card-text">Quantity: {{ training.quantity }}<button class="btn btn-primary float-end">Ajouter</button></p>

          </div>
        </div>
      </div>
    </div>
    <p class="error-message" *ngIf="error">{{ error }}</p>
  </div>
  <div class="container">
    <h2>Ajouter un nouveau training</h2>
    <form (ngSubmit)="createTraining()">
      <div class="form-group">
        <label for="name">Nom</label>
        <input type="text" class="form-control" id="name" [(ngModel)]="training.name" name="name" required>
      </div>
      <div class="form-group">
        <label for="description">Description</label>
        <textarea class="form-control" id="description" [(ngModel)]="training.description" name="description" required></textarea>
      </div>
      <div class="form-group">
        <label for="price">Prix</label>
        <input type="number" class="form-control" id="price" [(ngModel)]="training.price" name="price" required>
      </div>
      <div class="form-group">
        <label for="quantity">Quantité</label>
        <input type="number" class="form-control" id="quantity" [(ngModel)]="training.quantity" name="quantity" required>
      </div>
      <button type="submit" class="btn btn-primary">Créer</button>
    </form>
  </div>
