import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs/internal/Observable';
import { environment } from 'src/app/environment/environment';
import { Training } from 'src/app/models/training';
import { catchError } from 'rxjs';

@Injectable({
  providedIn: 'root',
})
export class TrainingService {
  constructor(private http: HttpClient) {}

  postTraining(training: Training): Observable<any> {
    const formData = new FormData();
    formData.append('name', training.name);
    formData.append('description', training.description);
    formData.append('price', training.price.toString());
    formData.append('quantity', training.quantity.toString());
    formData.append('imagePath', training.imagePath);

    return this.http.post(environment.host + '/trainings', formData);
  }

  getTrainings(): Observable<Training[]> {
    return this.http.get<Training[]>(environment.host + '/trainings');
  }

  // getTrainingById(id: number): Observable<Training> {
  //   return this.http.get<Training>(environment.host + '/training' + id);
  // }

  // getTraining(id: number): Observable<Training> {
  //   const url = `http://localhost:8080/api/training/${id}`;
  //   return this.http.get<Training>(url);
  // }

  getTraining(id: number): Observable<Training> {
    const url = `http://localhost:8080/api/training/${id}`;
    return this.http.get<Training>(url).pipe(catchError(this.handleError));
  }
  handleError(
    handleError: any
  ): import('rxjs').OperatorFunction<Training, any> {
    throw new Error('Method not implemented.');
  }

  updateTraining(training: Training): Observable<Training> {
    const url = `http://localhost:8080/api/training/${training}`;
    return this.http.put<Training>(url, training);
  }

  deleteTraining(id: number): Observable<void> {
    const url = `${environment.host + '/training'}/${id}`;
    return this.http.delete<void>(url);
  }

  private handleError(error: HttpErrorResponse): Observable<never> {
    let errorMessage =
      "Une erreur s'est produite lors de la communication avec le serveur.";
    if (error.error instanceof ErrorEvent) {
      // Erreur côté client
      errorMessage = error.error.message;
    } else {
      // Erreur côté serveur
      errorMessage = error.status + ' - ' + error.statusText;
    }
    console.error(errorMessage);
    return throwError(errorMessage);
  }
}
