import { Component, OnInit, ViewChild } from '@angular/core';
import { Training } from 'src/app/models/training';
import { TrainingService } from 'src/app/services/training/training.service';
import { environment } from 'src/app/environment/environment';
import { Router } from '@angular/router';

@Component({
  selector: 'app-trainings',
  templateUrl: './trainings.component.html',
  styleUrls: ['./trainings.component.css'],
})
export class TrainingsComponent implements OnInit {
  @ViewChild('resetFormSave', { static: false }) resetFormSave: any;
  trainings: Training[] = [];
  training: Training = new Training();
  error!: string;
  public hostImageTrainings: string = environment.hostImageTrainings;

  constructor(
    private trainingService: TrainingService,
    private router: Router
  ) {}

  ngOnInit() {
    this.showTrainings();
  }

  showTrainings(): void {
    this.trainingService.getTrainings().subscribe({
      next: (data: Training[]) => {
        this.trainings = data;
      },
      error: (error) => {
        this.error =
          "Une erreur s'est produite lors du chargement des articles. /n" +
          error;
      },
    });
  }

  onFileChange(event: any) {
    const file = event.target.files[0];
    this.training.imagePath = file;
  }

  saveTraining(): void {
    this.trainingService.postTraining(this.training).subscribe({
      next: () => {
        console.log('Formation enregistrée avec succès');
        // this.showTrainings();
        setTimeout(() => {
          this.showTrainings();
          this.resetForm();
        }, 500);

        // Réinitialiser les champs
      },
      error: (error: string) => {
        this.error =
          "Erreur lors de l'enregistrement de la formation. \n" + error;
        this.showTrainings();
      },
    });
  }

  deleteTraining(training: Training): void {
    this.trainingService.deleteTraining(training.id).subscribe(
      () => {
        console.log('Formation supprimée avec succès');
        this.showTrainings();
      },
      (error) =>
        console.error(
          "Une erreur s'est produite lors de la suppression de la formation.",
          error
        )
    );
  }

  openUpdateForm(trainingId: any): void {
    this.router.navigate(['/updete-training', trainingId]);
  }

  // updateTraining(training: Training): void {
  //   this.trainingService.updateTraining(training).subscribe(
  //     (updatedTraining) => {
  //       console.log('updeteTaining : ' + training);
  //       console.log('Training updated successfully:', updatedTraining);
  //       // Mise à jour de la liste des trainings affichés
  //       const index = this.trainings.findIndex(
  //         (t) => t.id === updatedTraining.id
  //       );
  //       if (index !== -1) {
  //         this.trainings[index] = updatedTraining;
  //       }
  //     },
  //     (error) => {
  //       console.error('Error updating training:', error);
  //     }
  //   );
  // }

  /**
   * Réinitialiser les champs du formulaire de création de cour.
   */
  resetForm(): void {
    this.resetFormSave.reset();
  }
}
<div class="container">
    <h1> Sélectionnez des cours.</h1>

    <div class="row">
      <div class="col-md-4"  *ngFor="let training of trainings">
        <div class="card mb-3">
            <img [src]="hostImageTrainings + training.imagePath" alt="Training Image"> 
          <div class="card-body">
            <h5 class="card-title">Language : {{ training.name }}</h5>
            <p class="card-text">{{ training.description }}</p>
            <p class="card-text">Price: {{ training.price }}</p>
            <p class="card-text">Quantity: {{ training.quantity }}<button class="btn btn-danger float-end mx-2" (click)="deleteTraining(training)">Supprimer</button><button class="btn btn-primary float-end mx-2" (click)="openUpdateForm(training.id)">Modifier</button></p>

          </div>
        </div>
      </div>
    </div>
    <p class="error-message" *ngIf="error">{{ error }}</p>
  </div>
  <div class="container">
    <h2>Ajouter un nouveau training</h2>
    <form (ngSubmit)="saveTraining()" #resetFormSave="ngForm">
      <div class="form-group my-3">
        <label for="name">Nom</label>
        <input type="text" class="form-control" id="name" placeholder="Nom" [(ngModel)]="training.name" name="name" required>
      </div>
      <div class="form-group my-3">
        <label for="description">Description</label>
        <textarea class="form-control" id="description" placeholder="Description" [(ngModel)]="training.description" name="description" required></textarea>
      </div>
      <div class="form-group my-3">
        <label for="price">Prix</label>
        <input type="number" class="form-control" id="price" placeholder="Prix" [(ngModel)]="training.price" name="price" required>
      </div>
      <div class="form-group my-3">
        <label for="quantity">Quantité</label>
        <input type="number" class="form-control" id="quantity" placeholder="Quantité" [(ngModel)]="training.quantity" name="quantity" required>
      </div>
      <div class="form-group my-4">
        <label for="image">Image :</label>
      <input type="file" class="form-control-file mx-3" id="image" (change)="onFileChange($event)" accept="image/*" required>
      </div>
      <div>
        <button type="submit" class="btn btn-primary float-start mb-5">Enregistrer</button>
      </div>
    </form>
  </div>
