import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Training } from '../models/training.model';
import { TrainingService } from '../services/training.service';

@Component({
  selector: 'app-update-training',
  templateUrl: './update-training.component.html',
  styleUrls: ['./update-training.component.css'],
})
export class UpdateTrainingComponent implements OnInit {
  training: Training;

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private trainingService: TrainingService
  ) {}

  ngOnInit(): void {
    const trainingId = +this.route.snapshot.paramMap.get('id');
    this.getTraining(trainingId);
  }

  getTraining(trainingId: number): void {
    this.trainingService
      .getTraining(trainingId)
      .subscribe((training) => (this.training = training));
  }

  updateTraining(): void {
    this.trainingService.updateTraining(this.training).subscribe(() => {
      console.log('Formation mise à jour avec succès');
      this.router.navigate(['/trainings']);
    });
  }
}

