import { Component, Input, Output, EventEmitter } from '@angular/core';
import { Training } from 'src/app/models/training';
import { TrainingService } from 'src/app/services/training/training.service';

@Component({
  selector: 'app-updete-training',
  templateUrl: './updete-training.component.html',
  styleUrls: ['./updete-training.component.css'],
})
export class UpdeteTrainingComponent {
  @Input() training: Training;
  @Output() updateTraining: EventEmitter<Training> = new EventEmitter();
  constructor(private trainingService: TrainingService) {
    this.training = new Training();
  }

  onUpdateTraining(): void {
    this.updateTraining.emit(this.training);
  }
}
