import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { environment } from 'src/app/environment/environment';
import { Training } from 'src/app/models/training';
import { TrainingService } from 'src/app/services/training/training.service';

@Component({
  selector: 'app-updete-training',
  templateUrl: './updete-training.component.html',
  styleUrls: ['./updete-training.component.css'],
})
export class UpdeteTrainingComponent implements OnInit {
  training: Training = new Training();
  public hostImageTrainings: string = environment.hostImageTrainings;

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private trainingService: TrainingService
  ) {}

  ngOnInit(): void {
    const trainingId = +this.route.snapshot.paramMap.get('id') | null;
    this.getTraining(trainingId);
  }

  getTraining(trainingId: number): void {
    this.trainingService
      .getTraining(trainingId)
      .subscribe((training) => (this.training = training));
  }

  updeteTraining(): void {
    this.trainingService.updateTraining(this.training).subscribe(() => {
      console.log('Formation mise à jour avec succès');
      this.router.navigate(['/trainings']);
    });
  }
}
