import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root',
})
export class ErrorHandlingService {
  constructor() {}

  handleHttpError(error: any): void {
    let errorMessage =
      "Une erreur s'est produite lors du traitement de la requête.";

    // Vérifiez le type d'erreur et personnalisez le message d'erreur si nécessaire
    if (error.status === 404) {
      errorMessage = 'La ressource demandée est introuvable.';
    } else if (error.status === 500) {
      errorMessage = "Une erreur interne du serveur s'est produite.";
    }
  }
}
